CREATE TABLE `shops` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT,
  `slug` varchar(255) DEFAULT NULL,
  `uuid` char(36) NOT NULL,
  `user_id` bigint unsigned NOT NULL,
  `tax` double(22,2) NOT NULL DEFAULT '0.00',
  `percentage` double NOT NULL DEFAULT '0',
  `location` varchar(255) DEFAULT NULL,
  `phone` varchar(255) DEFAULT NULL,
  `show_type` tinyint DEFAULT NULL,
  `open` tinyint(1) NOT NULL DEFAULT '1',
  `visibility` tinyint(1) NOT NULL DEFAULT '1',
  `background_img` varchar(191) DEFAULT NULL,
  `logo_img` varchar(191) DEFAULT NULL,
  `min_amount` double NOT NULL DEFAULT '0.1',
  `status` enum('new','edited','approved','rejected','inactive') NOT NULL DEFAULT 'new',
  `type` enum('retail','agri','all') DEFAULT NULL,
  `status_note` text,
  `delivery_time` longtext NOT NULL,
  `price` double NOT NULL DEFAULT '0',
  `price_per_km` double NOT NULL DEFAULT '0',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `deleted_at` timestamp NULL DEFAULT NULL,
  `service_fee` smallint DEFAULT '0',
  `verify` tinyint(1) NOT NULL DEFAULT '0',
  `order_payment` varchar(255) NOT NULL DEFAULT 'before',
  `email_statuses` varchar(255) DEFAULT NULL,
  `wifi_password` varchar(255) DEFAULT NULL,
  `wifi_name` varchar(255) DEFAULT NULL,
  `new_order_after_payment` tinyint(1) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `shops_user_id_foreign` (`user_id`),
  KEY `shops_uuid_index` (`uuid`)
) ENGINE=InnoDB AUTO_INCREMENT=9015 DEFAULT CHARSET=utf8mb3;

